mapper.select:
SELECT B.* FROM(
	SELECT rownum rn, A.* FROM(
		SELECT * FROM notice order by noticeNum desc
	)A
)B WHERE rn BETWEEN #{b} and #{e}

SELECT B.* FROM(
	SELECT rownum rn, A.* FROM(
		SELECT * FROM notice

			WHERE title LIKE '%'||#{search}||'%'
			WHERE content LIKE '%'||#{search}||'%'
			WHERE title LIKE '%'||#{search}||'%' or content LIKE '%'||#{search}||'%' 

	)A
)B WHERE rn BETWEEN #{b} and #{e}
